import time
import subprocess
import psutil
import threading
import sys
import webbrowser
from PIL import Image
import pystray
import ctypes
import ctypes.wintypes
import os
import base64
import io

ICON_DATA = b""""""

def load_embedded_icon():
    return Image.open(io.BytesIO(base64.b64decode(ICON_DATA)))

# Tool configurations - using environment variables for user paths
AWAKENED_EXE = os.path.join(
    os.environ["LOCALAPPDATA"],
    "Programs",
    "Awakened PoE Trade",
    "Awakened Poe Trade.exe"
)
AWAKENED_NAME = "Awakened Poe Trade.exe"
AWAKENED_DOWNLOAD_URL = "https://snosme.github.io/awakened-poe-trade/download"

EXILED_EXCHANGE_2_EXE = os.path.join(
    os.environ["LOCALAPPDATA"],
    "Programs",
    "Exiled Exchange 2",
    "Exiled Exchange 2.exe"
)
EXILED_EXCHANGE_2_NAME = "Exiled Exchange 2.exe"
EXILED_EXCHANGE_2_DOWNLOAD_URL = "https://kvan7.github.io/Exiled-Exchange-2/download"

delay_seconds = 15
watcher_enabled = True
notifications_enabled = True
force_run_next = False
stop_flag = False
watcher_thread = None

def notify(icon, text):
    if notifications_enabled and icon:
        icon.notify(text, "poe watcher")

def process_running(name):
    for p in psutil.process_iter(['name']):
        try:
            if p.info['name'] and p.info['name'].lower() == name.lower():
                return True
        except:
            pass
    return False

def get_window_title_by_process_name(process_name):
    """Get window title for a given process name using Windows API"""
    EnumWindows = ctypes.windll.user32.EnumWindows
    EnumWindowsProc = ctypes.WINFUNCTYPE(ctypes.c_bool, ctypes.POINTER(ctypes.c_int), ctypes.POINTER(ctypes.c_int))
    GetWindowText = ctypes.windll.user32.GetWindowTextW
    GetWindowTextLength = ctypes.windll.user32.GetWindowTextLengthW
    IsWindowVisible = ctypes.windll.user32.IsWindowVisible
    GetWindowThreadProcessId = ctypes.windll.user32.GetWindowThreadProcessId
    
    found_titles = []
    target_pids = []
    
    # Get all PIDs for the process name
    for proc in psutil.process_iter(['name', 'pid']):
        try:
            if proc.info['name'] and proc.info['name'].lower() == process_name.lower():
                target_pids.append(proc.info['pid'])
        except:
            pass
    
    def callback(hwnd, lParam):
        if IsWindowVisible(hwnd):
            pid = ctypes.c_ulong()
            GetWindowThreadProcessId(hwnd, ctypes.byref(pid))
            
            if pid.value in target_pids:
                length = GetWindowTextLength(hwnd)
                buff = ctypes.create_unicode_buffer(length + 1)
                GetWindowText(hwnd, buff, length + 1)
                title = buff.value
                if title:
                    found_titles.append(title)
        return True
    
    EnumWindows(EnumWindowsProc(callback), 0)
    return found_titles[0] if found_titles else None

def detect_poe_version():
    """
    Detect if PoE 1 or PoE 2 is running by checking window titles
    Returns: 1 for PoE 1, 2 for PoE 2, None if neither
    """
    poe_candidates = [
        "PathOfExile.exe",
        "PathOfExile_x64.exe",
        "PathOfExileSteam.exe",
        "PathOfExile_x64Steam.exe",
    ]
    
    for exe in poe_candidates:
        if process_running(exe):
            # Get window title
            title = get_window_title_by_process_name(exe)
            if title:
                title_lower = title.lower()
                # PoE 2 usually has "Path of Exile 2" in the title
                if "path of exile 2" in title_lower or "poe2" in title_lower:
                    return (2, exe)
                # PoE 1 might just have "Path of Exile" without the "2"
                elif "path of exile" in title_lower:
                    return (1, exe)
            # If we can't get the title, check the executable name
            # PoE 2 executables might have specific patterns
            if "2" in exe.lower():
                return (2, exe)
            return (1, exe)
    
    return (None, None)

def kill_process(name):
    for p in psutil.process_iter(['name']):
        try:
            if p.info['name'] and p.info['name'].lower() == name.lower():
                p.kill()
        except:
            pass

def start_tool_for_poe_version(poe_version, icon):
    """Start the appropriate tool based on PoE version, or open download page if not installed"""
    if poe_version == 2:
        if not os.path.exists(EXILED_EXCHANGE_2_EXE):
            notify(icon, "exiled exchange 2 not installed, opening download page...")
            webbrowser.open(EXILED_EXCHANGE_2_DOWNLOAD_URL)
        elif not process_running(EXILED_EXCHANGE_2_NAME):
            subprocess.Popen(EXILED_EXCHANGE_2_EXE)
            notify(icon, "exiled exchange 2 started")
    elif poe_version == 1:
        if not os.path.exists(AWAKENED_EXE):
            notify(icon, "awakened not installed, opening download page...")
            webbrowser.open(AWAKENED_DOWNLOAD_URL)
        elif not process_running(AWAKENED_NAME):
            subprocess.Popen(AWAKENED_EXE)
            notify(icon, "awakened started")

def watcher_loop(icon):
    global force_run_next
    current_poe_version = None
    
    while not stop_flag:
        if not watcher_enabled:
            time.sleep(1)
            continue
        
        poe_version, poe_exe = detect_poe_version()
        
        if not poe_version:
            # No PoE detected, kill both tools
            kill_process(AWAKENED_NAME)
            kill_process(EXILED_EXCHANGE_2_NAME)
            current_poe_version = None
            time.sleep(1)
            continue
        
        # If PoE version changed, kill all tools
        if current_poe_version and current_poe_version != poe_version:
            kill_process(AWAKENED_NAME)
            kill_process(EXILED_EXCHANGE_2_NAME)
        
        current_poe_version = poe_version
        time.sleep(delay_seconds)
        
        # Determine which tool should be running
        if poe_version == 2:
            target_tool = EXILED_EXCHANGE_2_NAME
            wrong_tool = AWAKENED_NAME
        else:
            target_tool = AWAKENED_NAME
            wrong_tool = EXILED_EXCHANGE_2_NAME
        
        # Kill the wrong tool if it's running
        if process_running(wrong_tool):
            kill_process(wrong_tool)
        
        # Start the correct tool if needed
        allowed = force_run_next or not process_running(target_tool)
        force_run_next = False
        if allowed:
            start_tool_for_poe_version(poe_version, icon)
            poe_name = "path of exile 2" if poe_version == 2 else "path of exile"
            notify(icon, f"detected {poe_name} ({poe_exe})")
        
        # Wait while PoE is running
        while detect_poe_version()[0] and not stop_flag:
            time.sleep(1)
        
        # Clean up when PoE exits
        kill_process(AWAKENED_NAME)
        kill_process(EXILED_EXCHANGE_2_NAME)
        current_poe_version = None

def menu_restart_watcher(icon, item):
    global watcher_thread, stop_flag
    stop_flag = True
    time.sleep(0.2)
    stop_flag = False
    watcher_thread = threading.Thread(target=watcher_loop, args=(icon,), daemon=True)
    watcher_thread.start()
    notify(icon, "watcher restarted")

def menu_start_awakened(icon, item):
    if not os.path.exists(AWAKENED_EXE):
        notify(icon, "awakened not installed, opening download page...")
        webbrowser.open(AWAKENED_DOWNLOAD_URL)
    elif not process_running(AWAKENED_NAME):
        subprocess.Popen(AWAKENED_EXE)
        notify(icon, "awakened started")

def menu_start_exiled(icon, item):
    if not os.path.exists(EXILED_EXCHANGE_2_EXE):
        notify(icon, "exiled exchange 2 not installed, opening download page...")
        webbrowser.open(EXILED_EXCHANGE_2_DOWNLOAD_URL)
    elif not process_running(EXILED_EXCHANGE_2_NAME):
        subprocess.Popen(EXILED_EXCHANGE_2_EXE)
        notify(icon, "exiled exchange 2 started")

def menu_kill_tools(icon, item):
    kill_process(AWAKENED_NAME)
    kill_process(EXILED_EXCHANGE_2_NAME)
    notify(icon, "all tools killed")

# Dummy action that does nothing
def do_nothing(icon, item):
    pass

# Status menu items with dynamic text
def get_poe_status_text(item):
    poe_version, poe_exe = detect_poe_version()
    if poe_version:
        poe_name = "PoE 2" if poe_version == 2 else "PoE 1"
        return f" {poe_name} running"
    return " not running"

def get_awakened_status_text(item):
    installed = os.path.exists(AWAKENED_EXE)
    running = process_running(AWAKENED_NAME)
    
    if installed and running:
        return " Awakened: installed & running"
    elif installed:
        return " Awakened: installed"
    else:
        return " Awakened: not installed"

def get_exiled_status_text(item):
    installed = os.path.exists(EXILED_EXCHANGE_2_EXE)
    running = process_running(EXILED_EXCHANGE_2_NAME)
    
    if installed and running:
        return " Exiled Ex 2: installed & running"
    elif installed:
        return " Exiled Ex 2: installed"
    else:
        return " Exiled Ex 2: not installed"

def menu_delay_5(icon, item):
    global delay_seconds
    delay_seconds = 5
    notify(icon, "delay set to 5 seconds")

def menu_delay_10(icon, item):
    global delay_seconds
    delay_seconds = 10
    notify(icon, "delay set to 10 seconds")

def menu_delay_15(icon, item):
    global delay_seconds
    delay_seconds = 15
    notify(icon, "delay set to 15 seconds")

def menu_toggle_watching(icon, item):
    global watcher_enabled
    watcher_enabled = not watcher_enabled
    notify(icon, "watcher resumed" if watcher_enabled else "watcher suspended")

def menu_force_run_next(icon, item):
    global force_run_next
    force_run_next = True
    notify(icon, "tool will force-start next poe session")

def menu_toggle_notifications(icon, item):
    global notifications_enabled
    notifications_enabled = not notifications_enabled
    notify(icon, f"notifications {'enabled' if notifications_enabled else 'disabled'}")

def menu_exit(icon, item):
    global stop_flag
    stop_flag = True
    icon.stop()
    sys.exit(0)

def make_icon():
    global watcher_thread
    img = load_embedded_icon()
    icon = pystray.Icon(
        "poe watcher",
        img,
        "poe awakened/exiled watcher",
        menu=pystray.Menu(
            pystray.MenuItem("restart watcher", menu_restart_watcher),
            pystray.MenuItem("start awakened now", menu_start_awakened),
            pystray.MenuItem("start exiled exchange 2 now", menu_start_exiled),
            pystray.MenuItem("kill all tools", menu_kill_tools),
            pystray.MenuItem(
                "check status",
                pystray.Menu(
                    pystray.MenuItem(get_poe_status_text, do_nothing),
                    pystray.MenuItem(get_awakened_status_text, do_nothing),
                    pystray.MenuItem(get_exiled_status_text, do_nothing)
                )
            ),
            pystray.MenuItem(
                "set delay",
                pystray.Menu(
                    pystray.MenuItem("5 seconds", menu_delay_5, checked=lambda item: delay_seconds == 5),
                    pystray.MenuItem("10 seconds", menu_delay_10, checked=lambda item: delay_seconds == 10),
                    pystray.MenuItem("15 seconds", menu_delay_15, checked=lambda item: delay_seconds == 15)
                )
            ),
            pystray.MenuItem(
                "watching enabled",
                menu_toggle_watching,
                checked=lambda item: watcher_enabled
            ),
            pystray.MenuItem(
                "force run next time",
                menu_force_run_next
            ),
            pystray.MenuItem(
                "notifications enabled",
                menu_toggle_notifications,
                checked=lambda item: notifications_enabled
            ),
            pystray.MenuItem("exit", menu_exit)
        )
    )
    watcher_thread = threading.Thread(target=watcher_loop, args=(icon,), daemon=True)
    watcher_thread.start()
    icon.run()

if __name__ == "__main__":
    make_icon()